  public String getSub(String hex) {
    //<editor-fold defaultstate="collapsed" desc="IF DEBUG">
    boolean DEBUG = false;
    if (DEBUG) { // true or false
      System.out.print(GlobalMethods.comment("=", 80));
      System.out.println("\t" + Thread.currentThread().getStackTrace()[1]
              .getMethodName());
    }
    //</editor-fold>
    int x = Integer.parseInt(hex.substring(0, 2));
    int y = Integer.parseInt(hex.substring(2, 4));
    String l = "";
    if (x < 9) {
      if (y < 11) {
        l = "A";
      }
      if (y > 10 & y < 21) {
        l = "E";
      }
      if (y > 20 & y < 31) {
        l = "I";
      }
      if (y > 30) {
        l = "M";
      }
    }
    if (x > 8 & x < 17) {
      if (y < 11) {
        l = "B";
      }
      if (y > 10 & y < 21) {
        l = "F";
      }
      if (y > 20 & y < 31) {
        l = "J";
      }
      if (y > 30) {
        l = "N";
      }
    } else if (x > 16 & x < 25) {
      if (y < 11) {
        l = "C";
      } else if (y > 10 & y < 21) {
        l = "G";
      } else if (y > 20 & y < 31) {
        l = "K";
      } else if (y > 30) {
        l = "O";
      }
    } else if (x > 24 & x < 33) {
      if (y < 11) {
        l = "D";
      } else if (y > 10 & y < 21) {
        l = "H";
      } else if (y > 20 & y < 31) {
        l = "L";
      } else if (y > 30) {
        l = "P";
      }
    }
    return l;
  }

