iewer.jfc.showSaveDialog(jf);//        if (ret == JFileChooser.APPROVE_OPTION) {////        }      }    });    jf.setDefaultCloseOperation(JFrame.HIDE_ON_CLOSE);    jf.setBounds(200, 0, 420, 700);    jf.setLayout(new BorderLayout());    //AnyMenu am = new AnyMenu(jf);    JPanel p4c = new Panel4Careers(args);    jf.getContentPane().add(p4c, BorderLayout.CENTER);    //jf.setJMenuBar(am);    jf.setVisible(true);  }  //</editor-fold>  /**   Frame4Careers   */  public Frame4Careers() {    super();    //<editor-fold defaultstate="collapsed" desc="DEBUG">    boolean DEBUG = false;    if (DEBUG) { // true or false      //System.out.print("\t" + Thread.currentThread().getStackTrace()[1].getMethodName());      ////System.out.print("" + " = " + );    }    //</editor-fold>    setDefaultCloseOperation(JFrame.HIDE_ON_CLOSE);    setBounds(200, 0, 420, 600);    setLayout(new BorderLayout());    final Panel4Careers p4c = new Panel4Careers();    getContentPane().add(p4c, BorderLayout.CENTER);    addWindowListener(new WindowAdapter() {      @Override      public void windowActivated(WindowEvent arg0) {        dbviewer.DBViewer.setTitle(panels.Panel4Careers.updateName());      }      @Override      public void windowClosed(WindowEvent arg0) {        p4c.removeAll();        removeAll();      }    });    setVisible(true);    GlobalMethods.autoColor(p4c, Color.black, Color.LIGHT_GRAY);  }  /**   <p>   @param name setTitle()   */  @Override  public void setTitle(String name) {    //<editor-fold defaultstate="collapsed" desc="DEBUG">    boolean DEBUG = false;    if (DEBUG) { // true or false      //System.out.print("\tClass " + " = " + getClass());      //System.out.print("\t" + Thread.currentThread().getStackTrace()[1].getMethodName());      ////System.out.print("" + " = " + );    }    //</editor-fold>    setTitle(name);  }  /**   <p>   @return name of class   */  @Override  public String toString() {    return "" + getClass().getName() + "[]";  }}