    ////System.out.print("" + " = " + );    }//</editor-fold>    if (!jf.isVisible()) {      jf.setVisible(true);    }    if (jl == Panel4SubSector.jList1) {      jta.setText("");    }    if (jl == Panel4SubSector.jList2) {      jta.append("  JUMP " + Panel4SubSector.jSlider1.getValue()        + " (KCr" + Panel4SubSector.jLabel1.getText().substring(0,          Panel4SubSector.jLabel1.getText().length() - 3) + ")" + crlf);      jta.append(Panel4Locator.getSystem() + "("        + Panel4SubSector.jcb4Trade.getSelectedItem().toString()        + //Panel4SubSector.jComboBox6.getSelectedItem().toString() +        ")" + crlf + crlf);    }  }  static public void write(String txt) {    jta.append(txt);  }  static public void writeLine(String txt) {    jta.append(txt + GlobalVariables.CRLF);  }}